mapper.select:
SELECT

FROM plugin_form a

WHERE a.id = #{id}

select.if:
select relname as name,cast(obj_description(relfilenode,'pg_class') as varchar) as comments from pg_class c where relname in (select tablename from pg_tables where schemaname='public' and position('_2' in tablename)=0)
AND relname = #{name}
order by name

SELECT t.TABLE_NAME AS name, c.COMMENTS AS comments FROM user_tables t, user_tab_comments c WHERE t.table_name = c.table_name
AND t.TABLE_NAME = #{name}
ORDER BY t.TABLE_NAME

SELECT t.table_name AS name,t.TABLE_COMMENT AS comments FROM information_schema.`TABLES` t WHERE t.TABLE_SCHEMA = (select database())
AND t.TABLE_NAME = #{name}
ORDER BY t.TABLE_NAME

select sysobjects.name as name ,sys.extended_properties.value as comments from sysobjects left join sys.extended_properties on sysobjects.id=sys.extended_properties.major_id and (sys.extended_properties.minor_id = '0' or sys.extended_properties.minor_id is null) where value in('U','V')
AND sysobjects.name = #{name}
order by name

SELECT t.COLUMN_NAME AS name, (CASE WHEN t.NULLABLE = 'Y' THEN '1' ELSE '0' END) AS isNull, (t.COLUMN_ID * 10) AS sort, c.COMMENTS AS comments, decode(t.DATA_TYPE,'DATE',t.DATA_TYPE || '(' || t.DATA_LENGTH || ')', 'VARCHAR2', t.DATA_TYPE || '(' || t.DATA_LENGTH || ')', 'VARCHAR', t.DATA_TYPE || '(' || t.DATA_LENGTH || ')', 'NVARCHAR2', t.DATA_TYPE || '(' || t.DATA_LENGTH/2 || ')', 'CHAR', t.DATA_TYPE || '(' || t.DATA_LENGTH || ')', 'NUMBER',t.DATA_TYPE || (nvl2(t.DATA_PRECISION,nvl2(decode(t.DATA_SCALE,0,null,t.DATA_SCALE), '(' || t.DATA_PRECISION || ',' || t.DATA_SCALE || ')', '(' || t.DATA_PRECISION || ')'),'(18)')),t.DATA_TYPE) AS jdbcType FROM user_tab_columns t, user_col_comments c WHERE t.TABLE_NAME = c.table_name AND t.COLUMN_NAME = c.column_name
AND t.TABLE_NAME = #{name}
ORDER BY t.COLUMN_ID

SELECT t.COLUMN_NAME AS name, (CASE WHEN t.IS_NULLABLE = 'YES' THEN '1' ELSE '0' END) AS isNull, (t.ORDINAL_POSITION * 10) AS sort,t.COLUMN_COMMENT AS comments,t.COLUMN_TYPE AS jdbcType FROM information_schema.`COLUMNS` t WHERE t.TABLE_SCHEMA = (select database())
AND t.TABLE_NAME = #{name}
ORDER BY t.ORDINAL_POSITION
